/*CREATE SCHEMA prdwa17_staging; -- Made for prior 'staging' before ETL to targetCREATE SCHEMA prdwa17_target; -- Made for dimensions and factsCREATE SCHEMA prdwa17_ml; -- Made for machines learning results calculated from target*/DROP TABLE prdwa17_staging.channels;DROP TABLE prdwa17_staging.videos;DROP TABLE prdwa17_staging.videosComments;DROP TABLE prdwa17_staging.YouTubeTags;CREATE TABLE prdwa17_staging.channels (	id VARCHAR(100),	title VARCHAR(255),	description TEXT,	publishedAt TIMESTAMP,	viewCount BIGINT,	commentCount BIGINT,	subscriberCount BIGINT,	videoCount BIGINT,	topicCategory_1 VARCHAR(255),	topicCategory_2 VARCHAR(255),	topicCategory_3 VARCHAR(255),	keywords TEXT,	PRIMARY KEY(id));CREATE TABLE prdwa17_staging.videos (-- 	dbId BIGSERIAL LOCAL,	id VARCHAR(100),	channelId VARCHAR(50),	title VARCHAR(255),	description TEXT,	publishedAt TIMESTAMP,	viewCount BIGINT,	commentCount BIGINT,	likeCount BIGINT,	dislikeCount BIGINT,	favoriteCount BIGINT,	categoryId VARCHAR(10),	topicCategory_1 VARCHAR(255),	topicCategory_2 VARCHAR(255),	topicCategory_3 VARCHAR(255),	PRIMARY KEY(id));CREATE TABLE prdwa17_staging.YouTubeTags (	id INT,	title VARCHAR(255),	videoId VARCHAR(100),	PRIMARY KEY(id));CREATE TABLE prdwa17_staging.videosComments (	-- dbId BIGSERIAL LOCAL PRIMARY KEY,	id VARCHAR(100) PRIMARY KEY,	authorChannelUrl VARCHAR(100),	authorChannelId VARCHAR(100),	videoId VARCHAR(100),	parentId VARCHAR(100),	textDisplay TEXT,	likeCount INT,	publishedAt TIMESTAMP);